mapper.insert:
insert 
  into tb_asset
  (
    asset_no
  , asset_type
  , regist_date
  , asset_name
  , car_no
  , no_mem
  , status
  )
  values
  (
    seq_assetno.nextval
  , 'C'
  , #{registDate}
  , #{assetName}
  , #{carNo}
  , #{noMem}
  , #{status}
  )

mapper.update:
update
       tb_asset
   set regist_date = #{registDate}
      , asset_name = #{assetName}
      , car_no = #{carNo}
      , no_mem = #{noMem}
      , status = #{status}
      , modify_date = sysdate
 where asset_no  = #{assetNo}
